package com.germani.dit.node.repo.db.model

import org.springframework.data.annotation.Id
import org.springframework.data.mongodb.core.mapping.Document

@Document(collection = "node-graph")
class GraphEntity(
    var initNode: GraphUnitEntity
) {
    //TODO: id Long autogenerated
    @Id
    var id: String? = null

    override fun equals(other: Any?): Boolean {
        if (this === other) return true
        if (javaClass != other?.javaClass) return false

        other as GraphEntity

        if (initNode != other.initNode) return false
        if (id != other.id) return false

        return true
    }

    override fun hashCode(): Int {
        var result = initNode.hashCode()
        result = 31 * result + (id?.hashCode() ?: 0)
        return result
    }

    override fun toString(): String {
        return "GraphEntity(nodes=$initNode, id=$id)"
    }
}